import React, { useEffect, useState } from 'react';
import axios from 'axios';
import PropertyCard from '../components/PropertyCard';

export default function Home() {
  const [listings, setListings] = useState([]);
  const [filters, setFilters] = useState({ location: '', minPrice: '', maxPrice: '', date: '' });

  const fetchListings = async () => {
    const params = {};
    if (filters.location) params.location = filters.location;
    if (filters.minPrice) params.minPrice = filters.minPrice;
    if (filters.maxPrice) params.maxPrice = filters.maxPrice;
    if (filters.date) params.date = filters.date;
    const res = await axios.get('http://localhost:5000/api/listings', { params });
    setListings(res.data);
  };

  useEffect(() => { fetchListings(); }, []); // initial load

  const handleChange = e => setFilters({ ...filters, [e.target.name]: e.target.value });
  const handleSearch = e => { e.preventDefault(); fetchListings(); };

  return (
    <div className="container">
      <h2>Properties</h2>
      <form style={{ display: 'flex', gap: 16, marginBottom: 24, flexWrap: 'wrap' }} onSubmit={handleSearch}>
        <input name="location" placeholder="Location" value={filters.location} onChange={handleChange} />
        <input name="minPrice" type="number" placeholder="Min Price" value={filters.minPrice} onChange={handleChange} />
        <input name="maxPrice" type="number" placeholder="Max Price" value={filters.maxPrice} onChange={handleChange} />
        <input name="date" type="date" value={filters.date} onChange={handleChange} />
        <button type="submit">Search</button>
      </form>
      <div className="property-list">
        {listings.map(listing => <PropertyCard key={listing._id} listing={listing} />)}
      </div>
    </div>
  );
}
