import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

export default function ListingDetail() {
  const { id } = useParams();
  const [listing, setListing] = useState(null);
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');
  const [message, setMessage] = useState('');

  useEffect(() => {
    axios.get(`http://localhost:5000/api/listings/${id}`)
      .then(res => setListing(res.data));
  }, [id]);

  const handleBooking = async e => {
    e.preventDefault();
    setMessage('');
    if (!localStorage.getItem('token')) {
      setMessage('Please login to book this property.');
      return;
    }
    if (!startDate || !endDate) {
      setMessage('Please select both start and end dates.');
      return;
    }
    if (startDate > endDate) {
      setMessage('Start date must be before end date.');
      return;
    }
    try {
      await axios.post('http://localhost:5000/api/bookings', {
        listingId: listing._id,
        startDate,
        endDate
      }, {
        headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
      });
      setMessage('Booking successful!');
    } catch (err) {
      setMessage(err.response?.data?.message || 'Booking failed');
    }
  };

  if (!listing) return <div>Loading...</div>;
  return (
    <div className="container">
      <h2 className="listing-title">{listing.title}</h2>
      <img src={listing.images[0]} alt="property" className="listing-image" />
      <p className="listing-description">{listing.description}</p>
      <p className="listing-location"><b>Location:</b> {listing.location}</p>
      <span className="listing-price">${listing.price} <span className="listing-night">/night</span></span>
      <div className="booking-section">
        <h4>Book this property</h4>
        <form onSubmit={handleBooking} className="booking-form">
          <label>Start Date:
            <input type="date" value={startDate} onChange={e => setStartDate(e.target.value)} min={listing.availableDates[0]} max={listing.availableDates[listing.availableDates.length-1]} />
          </label>
          <label>End Date:
            <input type="date" value={endDate} onChange={e => setEndDate(e.target.value)} min={listing.availableDates[0]} max={listing.availableDates[listing.availableDates.length-1]} />
          </label>
          <button type="submit" className="booking-btn">Book</button>
        </form>
        <div className={message === 'Booking successful!' ? 'booking-success' : 'booking-error'} style={{ minHeight: 24, fontWeight: 600 }}>{message}</div>
        <div className="listing-available-dates">
          <b>Available Dates:</b> {listing.availableDates.join(', ')}
        </div>
      </div>
    </div>
  );
}
