import React, { useEffect, useState } from 'react';
import axios from 'axios';
import PropertyCard from '../components/PropertyCard';

export default function Home() {
  const [listings, setListings] = useState([]);
  const [filters, setFilters] = useState({ q: '', location: '', minPrice: '', maxPrice: '', date: '' });
  const [loading, setLoading] = useState(false);

  const fetchListings = async () => {
    setLoading(true);
    const params = {};
    if (filters.q) params.q = filters.q;
    if (filters.location) params.location = filters.location;
    if (filters.minPrice) params.minPrice = filters.minPrice;
    if (filters.maxPrice) params.maxPrice = filters.maxPrice;
    if (filters.date) params.date = filters.date;
    const res = await axios.get('http://localhost:5000/api/listings', { params });
    setListings(res.data);
    setLoading(false);
  };

  useEffect(() => { fetchListings(); }, []); // initial load

  const handleChange = e => setFilters({ ...filters, [e.target.name]: e.target.value });
  const handleSearch = e => { e.preventDefault(); fetchListings(); };
  const handleClear = () => setFilters({ q: '', location: '', minPrice: '', maxPrice: '', date: '' });

  const isFiltered = Object.values(filters).some(val => val);

  return (
    <div className="container">
      <div style={{
        minHeight: '30vh',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        background: 'linear-gradient(120deg, #e0e7ef 0%, #f8fafc 100%)',
        borderRadius: 24,
        marginBottom: 40,
        boxShadow: '0 4px 32px rgba(60,60,120,0.10)',
        position: 'relative',
        overflow: 'visible',
        animation: 'fadeIn 1.1s cubic-bezier(.4,0,.2,1)'
      }}>
        <h2 style={{ fontSize: '2.6rem', fontWeight: 800, marginBottom: 10, letterSpacing: '-1.5px', color: '#222', textShadow: '0 2px 8px rgba(60,60,120,0.08)' }}>Find Your Next Stay</h2>
        <div style={{ color: '#666', marginBottom: 34, fontSize: '1.22rem', fontWeight: 500, textShadow: '0 1px 4px rgba(60,60,120,0.06)' }}>Browse beautiful places to stay, worldwide.</div>
        <form
          style={{
            display: 'flex',
            gap: 0,
            alignItems: 'center',
            background: '#fff',
            borderRadius: 999,
            padding: 14,
            boxShadow: '0 8px 32px rgba(40,60,120,0.13)',
            border: '1.5px solid #e3e8f0',
            maxWidth: 850,
            margin: '0 auto',
            flexWrap: 'wrap',
            position: 'relative',
            transition: 'box-shadow 0.2s',
            minWidth: 320,
          }}
          onSubmit={handleSearch}
        >
          {/* Main search input */}
          <span style={{ display: 'flex', alignItems: 'center', padding: '0 18px', fontWeight: 500, fontSize: 16, minWidth: 180 }}>
            <span role="img" aria-label="search" style={{ marginRight: 6, color: '#1976d2', fontSize: 20 }}>🔍</span>
            <input
              name="q"
              placeholder="Search by keyword, city, or title..."
              value={filters.q}
              onChange={handleChange}
              style={{
                border: 'none',
                outline: 'none',
                background: 'transparent',
                fontSize: 16,
                width: 160,
                padding: 0,
                minWidth: 100,
              }}
            />
          </span>
          <span style={{ height: 32, width: 1, background: '#e3e8f0', margin: '0 8px' }} />
          <span style={{ display: 'flex', alignItems: 'center', padding: '0 18px', fontWeight: 500, fontSize: 16 }}>
            <span role="img" aria-label="location" style={{ marginRight: 6 }}>📍</span>
            <input
              name="location"
              placeholder="Location (e.g. New York)"
              value={filters.location}
              onChange={handleChange}
              style={{
                border: 'none',
                outline: 'none',
                background: 'transparent',
                fontSize: 16,
                width: 110,
                padding: 0,
              }}
            />
          </span>
          <span style={{ height: 32, width: 1, background: '#e3e8f0', margin: '0 8px' }} />
          <span style={{ display: 'flex', alignItems: 'center', padding: '0 18px', fontWeight: 500, fontSize: 16 }}>
            <span role="img" aria-label="price" style={{ marginRight: 6 }}>💵</span>
            <input
              name="minPrice"
              type="number"
              placeholder="Min"
              value={filters.minPrice}
              onChange={handleChange}
              style={{ border: 'none', outline: 'none', background: 'transparent', fontSize: 16, width: 60, padding: 0 }}
            />
            <span style={{ margin: '0 4px', color: '#aaa' }}>-</span>
            <input
              name="maxPrice"
              type="number"
              placeholder="Max"
              value={filters.maxPrice}
              onChange={handleChange}
              style={{ border: 'none', outline: 'none', background: 'transparent', fontSize: 16, width: 60, padding: 0 }}
            />
          </span>
          <span style={{ height: 32, width: 1, background: '#e3e8f0', margin: '0 8px' }} />
          <span style={{ display: 'flex', alignItems: 'center', padding: '0 18px', fontWeight: 500, fontSize: 16 }}>
            <span role="img" aria-label="calendar" style={{ marginRight: 6 }}>📅</span>
            <input
              name="date"
              type="date"
              value={filters.date}
              onChange={handleChange}
              style={{ border: 'none', outline: 'none', background: 'transparent', fontSize: 16, width: 130, padding: 0 }}
            />
          </span>
          <button
            type="submit"
            style={{
              background: 'linear-gradient(90deg, #1976d2 60%, #63b3ed 100%)',
              color: '#fff',
              border: 'none',
              borderRadius: 999,
              padding: '0.7rem 2.2rem',
              fontWeight: 700,
              fontSize: 18,
              marginLeft: 12,
              cursor: 'pointer',
              boxShadow: '0 2px 8px rgba(25,118,210,0.10)',
              transition: 'background 0.2s, box-shadow 0.2s',
              minWidth: 120,
              display: 'flex', alignItems: 'center', justifyContent: 'center',
            }}
            disabled={loading}
          >
            {loading ? (
              <span style={{ display: 'flex', alignItems: 'center' }}>
                <span className="spinner" style={{ width: 20, height: 20, border: '2px solid #fff', borderRightColor: 'transparent', borderRadius: '50%', display: 'inline-block', marginRight: 8, animation: 'spin 0.7s linear infinite' }} />
                Searching...
              </span>
            ) : (
              <><span role="img" aria-label="search">🔍</span> Search</>
            )}
          </button>
          {isFiltered && (
            <button
              type="button"
              onClick={handleClear}
              style={{
                marginLeft: 14,
                background: 'none',
                color: '#1976d2',
                border: 'none',
                fontWeight: 600,
                fontSize: 16,
                cursor: 'pointer',
                padding: '0.7rem 1.3rem',
                borderRadius: 999,
                transition: 'background 0.15s',
                outline: 'none',
              }}
            >
              Clear Filters
            </button>
          )}
        </form>
      </div>
      <div className="property-list">
        {loading ? (
          <div style={{ color: '#1976d2', fontSize: '1.2rem', padding: '2rem', textAlign: 'center', width: '100%' }}>
            <span className="spinner" style={{ width: 26, height: 26, border: '3px solid #1976d2', borderRightColor: 'transparent', borderRadius: '50%', display: 'inline-block', marginRight: 12, animation: 'spin 0.7s linear infinite', verticalAlign: 'middle' }} />
            Loading listings...
          </div>
        ) : listings.length === 0 ? (
          <div style={{ color: '#888', fontSize: '1.2rem', padding: '2rem', textAlign: 'center', width: '100%' }}>
            No properties found. Try adjusting your filters.
          </div>
        ) : (
          listings.map(listing => <PropertyCard key={listing._id} listing={listing} />)
        )}
      </div>
    </div>
  );
}
