import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

export default function ListingDetail() {
  const { id } = useParams();
  const [listing, setListing] = useState(null);
  useEffect(() => {
    axios.get(`http://localhost:5000/api/listings/${id}`)
      .then(res => setListing(res.data));
  }, [id]);
  if (!listing) return <div>Loading...</div>;
  return (
    <div className="container">
      <h2>{listing.title}</h2>
      <img src={listing.images[0]} alt="property" style={{ width: '100%', maxWidth: 600 }} />
      <p>{listing.description}</p>
      <p><b>Location:</b> {listing.location}</p>
      <p><b>Price:</b> ${listing.price}/night</p>
      {/* Calendar/Booking UI would go here */}
    </div>
  );
}
