import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';

export default function Navbar() {
  const isLoggedIn = !!localStorage.getItem('token');
  const user = JSON.parse(localStorage.getItem('user') || '{}');
  const [darkMode, setDarkMode] = useState(false);

  // Sync dark mode state with localStorage and custom event
  useEffect(() => {
    const saved = localStorage.getItem('darkMode');
    if (saved === 'true') {
      setDarkMode(true);
      document.body.classList.add('dark-mode');
    } else {
      setDarkMode(false);
      document.body.classList.remove('dark-mode');
    }
    // Listen for dark mode changes from other components/tabs
    const handler = (e) => {
      setDarkMode(e.detail === true);
      if (e.detail === true) {
        document.body.classList.add('dark-mode');
      } else {
        document.body.classList.remove('dark-mode');
      }
    };
    window.addEventListener('darkmode-toggle', handler);
    return () => window.removeEventListener('darkmode-toggle', handler);
  }, []);

  useEffect(() => {
    if (darkMode) {
      document.body.classList.add('dark-mode');
      localStorage.setItem('darkMode', 'true');
    } else {
      document.body.classList.remove('dark-mode');
      localStorage.setItem('darkMode', 'false');
    }
    // Broadcast change to all listeners
    window.dispatchEvent(new CustomEvent('darkmode-toggle', { detail: darkMode }));
  }, [darkMode]);

  return (
    <nav className="navbar" style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between', padding: '1.2rem 2.2rem' }}>
      <Link to="/" style={{ fontWeight: 800, fontSize: '1.3rem', color: 'inherit', textDecoration: 'none', letterSpacing: '-1px' }}>StayFinder</Link>
      <div style={{ display: 'flex', alignItems: 'center', gap: 18 }}>
        {isLoggedIn ? (
          <>
            {user.isHost && <Link to="/host">Host Dashboard</Link>}
            <button onClick={() => { localStorage.removeItem('token'); localStorage.removeItem('user'); window.location = '/'; }}>Logout</button>
          </>
        ) : (
          <>
            <Link to="/login">Login</Link>
            <Link to="/register">Register</Link>
          </>
        )}
        <button
          onClick={() => setDarkMode(dm => !dm)}
          aria-label={darkMode ? 'Switch to light mode' : 'Switch to dark mode'}
          style={{
            background: darkMode ? 'linear-gradient(90deg, #23232b, #18181b)' : 'linear-gradient(90deg, #FF5A5F, #7B2FF2)',
            border: 'none',
            borderRadius: 999,
            width: 54,
            height: 30,
            padding: 0,
            display: 'flex',
            alignItems: 'center',
            position: 'relative',
            cursor: 'pointer',
            marginLeft: 18,
            transition: 'background 0.25s',
            boxShadow: darkMode ? '0 1px 8px #23232b44' : '0 1px 8px #ff5a5f22',
          }}
        >
          <span style={{
            position: 'absolute',
            left: darkMode ? 24 : 4,
            top: 3,
            width: 24,
            height: 24,
            borderRadius: '50%',
            background: darkMode ? '#23232b' : '#fff',
            boxShadow: '0 1px 4px #0001',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: 18,
            color: darkMode ? '#FF5A5F' : '#7B2FF2',
            transition: 'left 0.22s cubic-bezier(.4,0,.2,1), background 0.18s',
            zIndex: 2,
          }}>
            {darkMode ? 'üåô' : '‚òÄÔ∏è'}
          </span>
          <span style={{
            width: '100%',
            height: 18,
            borderRadius: 999,
            background: 'transparent',
            display: 'block',
            zIndex: 1,
          }} />
        </button>
      </div>
    </nav>
  );
}
